import React, { useState } from "react";


function InputsAreas(props) {

    const [note, setNote] = useState({
        title: "",
        content: "",


    });

    const [count, setCount] = useState(0)

    function handleText(event) {
        const { name, value } = event.target;
        setNote(prevText => {
            return {
                ...prevText,
                [name]: value,
            }

        });
    }

    function handleSubmit(event) {
       props.onSubmit(note);
       event.preventDefault();
       setNote({
        title: "",
        content: "",
    
      });
      setCount(count + 1)
      
    }
    return (
        <div>
            <p>{props.counter}</p>
            <form>
                <input onChange={handleText} name="title" value={note.title} placeholder="Title" />
                <textarea onChange={handleText} name="content" value={note.content} placeholder="Take a note..." rows="3" />
                <button onClick={handleSubmit}>Add</button>
            </form>
        </div>
    );
}

export default InputsAreas;
