import React, { useState } from "react";
import AddCircleRoundedIcon from '@material-ui/icons/AddCircleRounded';
import useForm from 'react-hook-form';



function InputsAreas(props) {

    const monthNames = ["January", "February", "March", "April", "May", "June",
        "July", "August", "September", "October", "November", "December"
    ];

    let today = new Date();
    let time = today.getHours() + ":" + today.getMinutes()
    let date = (monthNames[today.getMonth()]) + '-' + today.getDate() + "-" + today.getFullYear();
    let dateTime = date + " " + time;


    const [note, setNote] = useState({
        title: "",
        content: "",
        currentTime: ""
    });
    const [expand, setExpand] = useState(false)
    const { useForm, register } = useForm();


    function handleText(event) {
        const { name, value } = event.target;
        setNote(prevText => {
            return {
                ...prevText,
                [name]: value,
                currentTime: dateTime
            }
        });
    }

    function handleSubmit(event) {
        props.onSubmit(note);
        event.preventDefault();
        setExpand(false);
        setNote({
            title: "",
            content: "",

        });
    }
    function clickNote() {
        setExpand(true);
    }


    return (
        <div>
            <form style={{ backgroundColor: expand ? "rgba(34, 49, 63, 1)" : "white" }}>
                {expand ?
                    <input required onChange={handleText}
                        name="title" value={note.title}
                        placeholder="Title"
                        ref={register}
                    /> : null

                }

                <textarea
                    
                    onClick={clickNote}
                    onChange={handleText}
                    name="content"
                    value={note.content}
                    placeholder="Take a note..."
                    rows={expand ? 3 : 1}
                    ref={register}
                />
                <button onClick={handleSubmit}><AddCircleRoundedIcon /> </button>
            </form>
        </div>
    );
}

export default InputsAreas;
