import React, { useState } from "react";


function InputsAreas(props) {

    const [note, setNote] = useState({
        title: "",
        content: "",
    });

    const [count, setCount] = useState(0)
    const [expand, setExpand] = useState(false)


    function handleText(event) {
        const { name, value } = event.target;
        setNote(prevText => {
            return {
                ...prevText,
                [name]: value,
            }
        });
    }

    function handleSubmit(event) {
        props.onSubmit(note);
        event.preventDefault();
        setCount(count + 1)
        setExpand(false);
        //props.counter(count)
        setNote({
            title: "",
            content: "",

        });
    }
    function clickNote() {
        setExpand(true);
    }

    function normal() {
        setExpand(false);
    }

    return (
        <div>
            <p>{count}</p>
            <form style={{ backgroundColor: expand ? "rgba(34, 49, 63, 1)" : "white" }}>
                {expand ?
                    <input required onChange={handleText}
                        name="title" value={note.title}
                        placeholder="Title"
                        required="required"
                    /> : null

                }

                <textarea
                    required="required"
                    onClick={clickNote}
                    onChange={handleText}
                    onMouseOut={normal}
                    name="content"
                    value={note.content}
                    placeholder="Take a note..."
                    rows={expand ? 3 : 1}
                />
                <button onClick={handleSubmit}>Add</button>
            </form>
        </div>
    );
}

export default InputsAreas;
